package io.renren.modules.sys.controller;import io.renren.common.utils.JsonResult;import io.renren.modules.sys.entity.Tongji;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.ArrayList;import java.util.HashMap;import java.util.List;@RestController@RequestMapping("/data")public class DataController {    @RequestMapping("/get")    public JsonResult getStepByDeviceId(){        HashMap<String, Object> map = new HashMap<>();        ArrayList<String> cols = new ArrayList<>();        cols.add("belongTime");        cols.add("stepNum");        map.put("columns",cols);        List<Tongji> result = new ArrayList<>();        Tongji tj1 = new Tongji("1月1日",312);        result.add(tj1);        Tongji tj2 = new Tongji("1月2日",175);        result.add(tj2);        Tongji tj3 = new Tongji("1月3日",248);        result.add(tj3);        Tongji tj4 = new Tongji("1月4日",308);        result.add(tj4);        Tongji tj5 = new Tongji("1月5日",188);        result.add(tj5);        Tongji tj6 = new Tongji("1月6日",278);        result.add(tj6);        Tongji tj7 = new Tongji("1月7日",128);        result.add(tj7);        map.put("rows",result);        return JsonResult.success(map);    }}