package io.renren.modules.sys.entity;import com.alibaba.fastjson.annotation.JSONField;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.annotation.TableName;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.experimental.Accessors;import java.io.Serializable;import java.util.Date;@Data@EqualsAndHashCode(callSuper = false)@Accessors(chain = true)@TableName("user_group")public class UserGroup implements Serializable {    private static final long serialVersionUID = 1L;    /**     * id     */    @TableId(value = "id", type = IdType.AUTO)    private Integer id;    /**     * 分组名     */    private String groupName;    /**     * 创建时间     */    @JSONField(format = "yyyy-MM-dd HH-mm:ss")    private Date createTime;    /**     * 更新时间     */    private Date updateTime;    private Long userId;}